const initialState = {
  name: "",
  location: "",
  category: "",
  hours: "",
  score: 0,
  description: "",
};

export default function manageFoodtruckForm(state = initialState, action) {
  switch (action.type) {
    case "UPDATE_FOODTRUCK_FORM":
      return action.formData;
    case "RESET_FOODTRUCK_FORM":
      return initialState;
    default:
      return state;
    case "FETCH_FOODTRUCK":
      return {
        ...state,
        recipe: action.recipe,
      };
    case "ADD_FOODTRUCK":
      return {
        ...state,
        allRecipes: [...state.allRecipes, action.recipe],
      };
  }
}
