import React from "react";
import { Nav, Navbar } from "react-bootstrap";
import { logout } from "../actions/currentAccount";
import { connect } from "react-redux";

const NavBar = (props) => {
  const logoutRedirect = () => {
    props.logout();
    alert("You have successfully logged out. Have a nice day!");
    props.history.push("/");
  };

  const renderNavbar = () => {
    if (props.loggedIn) {
      debugger;
      return (
        <div>
          <Nav.Item>Hi, {props.currentAccount.account.name}</Nav.Item>
          <Nav.Item>
            <Nav.Link href="/">Home</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link onClick={logoutRedirect}>Logout</Nav.Link>
          </Nav.Item>
        </div>
      );
    } else {
      return (
        <div>
          <Nav.Item>
            <Nav.Link href="/">Home</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link href="/signup">Sign Up</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link href="/login">Login</Nav.Link>
          </Nav.Item>
        </div>
      );
    }
  };
  return (
    // <div>
    //   <Navbar expand="lg" fixed="top">
    //     <Nav className="ml-auto">{renderNavbar()}</Nav>
    //   </Navbar>
    // </div>

    <div>
      <Nav.Item>
        <Nav.Link href="/">Home</Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link href="/signup">Sign Up</Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link href="/login">Login</Nav.Link>
      </Nav.Item>
    </div>
  );
};
const mapStateToProps = (state) => {
  return {
    currentAccount: state.currentAccount,
    loggedIn: !!state.currentAccount,
  };
};
export default connect(mapStateToProps, { logout })(NavBar);
