import React from "react";
import { Nav, Navbar } from "react-bootstrap";
import { logout } from "../actions/currentAccount";
import { connect } from "react-redux";

const NavBar = (props) => {
  const logoutRedirect = () => {
    props.logout();
    alert("You have successfully logged out. Have a nice day!");
    props.history.push("/");
  };

  const renderNavbar = () => {
    if (props.loggedIn) {
      return (
        <div>
          <Navbar>
            <Nav.Item>Hi nadav</Nav.Item>
            <Nav.Item>
              <Nav.Link href="/">Home</Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link onClick={logoutRedirect}>Logout</Nav.Link>
            </Nav.Item>
          </Navbar>
        </div>
      );
    } else {
      return (
        <div>
          <Navbar bg="light" expand="lg">
  <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
            <Nav.Item>
              <Nav.Link href="/">Home</Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link href="/signup">Sign Up</Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link href="/login">Login</Nav.Link>
            </Nav.Item>
          </Navbar>
        </div>
      );
    }
  };
  return (
    <div>{renderNavbar}</div>

    // <div className="nav">
    //   <a href="/">Home</a>

    //   <a href="/signup">Sign Up</a>

    //   <a href="/login">Login</a>
    // </div>
  );
};
const mapStateToProps = (state) => {
  return {
    currentAccount: state.currentAccount,
    loggedIn: !!state.currentAccount,
  };
};
export default connect(mapStateToProps, { logout })(NavBar);
