import React, { Component } from "react";
import Navbar from "./components/Navbar";
// import Home from "./components/containers/Home";
import Login from "./components/sessions/Login";
import Home from './containers/Home'
import Signup from "./components/sessions/Signup";
import { connect } from "react-redux";
import { getCurrentAccount } from "./actions/currentAccount";
import { Route, Switch, withRouter, Redirect } from "react-router-dom";

class App extends Component {
  componentDidMount() {
    this.props.getCurrentAccount();
  }

  render() {
    return (
      <div className="App">
         <Navbar location={this.props.location} history={this.props.history} /> 
        <Switch>
          <Route
            exact
            path="/signup"
            render={({ history }) => <Signup history={history} />}
          />
          <Route exact path="/login" component={Login} />
          <Route exact path="/" component={Home} />
          <Redirect to="/no-match" />
        </Switch>
      </div>
    );
  }
}
const mapStateToProps = (state) => {};

export default withRouter(connect(null, { getCurrentAccount })(App));
