import React from "react";
import { Redirect } from "react-router-dom";
import M from "materialize-css";
import "materialize-css/dist/css/materialize.min.css";

class NewFoodtruck extends React.Component {
  state = {
    name: "",
    location: "",
    category: "",
    hours: "",
    score: 0,
    description: "",
    account_id: 0,
  };
  componentDidMount() {
    let rangeField = document.getElementById("input_rangefield");
    let nameField = document.getElementById("input_name");
    M.AutoInit(nameField, rangeField);
    M.CharacterCounter.init(nameField);
    // console.log(this.props);
    // if (this.props.foodTruck) {
    //   const { foodTruck } = this.props.foodTruck;
    //   this.setState({
    //     name: foodTruck.name,
    //     location: foodTruck.location,
    //     category: foodTruck.category,
    //     hours: foodTruck.hours,
    //     description: foodTruck.description,
    //     score: foodTruck.score,
    //   });
    // }
  }
  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
      account_id: this.props.account.account.action.id,
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    const accountId = this.state.account_id;
    if (this.props.ftId) {
      const foodTruckId = this.props.ftId;
      this.props.onSubmit(
        this.state,
        accountId,
        foodTruckId,
        this.props.history
      );
    } else {
      this.props.onSubmit(this.state, accountId, this.props.history);
    }
  };

  renderForm = () => {
    if (this.props.account.loggedIn) {
      return (
        <div
          className="section"
          style={{
            display: "flex",
            flexDirection: "column",
            minHeight: "150vh",
          }}
        >
          <div className="center">
            <div className="container">
              <h5 className="indigo-text">{this.props.buttonText}</h5>
              <div
                className="z-depth-1 grey lighten-4 container"
                style={{
                  display: "inline-block",
                  padding: "32px 48px 0px 48px",
                  border: "1px solid #EEE",
                }}
              >
                <form onSubmit={this.handleSubmit}>
                  <div className="row">
                    <div className="col s12">
                      <label htmlFor="name" className="left indigo-text">
                        Foodtruck's name:
                      </label>
                      <input
                        id="input_name"
                        maxLength="20"
                        data-length="20"
                        className="validate"
                        type="text"
                        name="name"
                        value={this.state.name}
                        onChange={this.handleChange}
                        placeholder={this.state.name}
                        required
                      ></input>
                    </div>
                  </div>
                  <div className="row">
                    <div className="col s12">
                      <label htmlFor="location" className="left indigo-text">
                        Foodtruck's location:
                      </label>
                      <input
                        className="validate"
                        maxLength="20"
                        data-length="20"
                        type="text"
                        name="location"
                        value={this.state.location}
                        onChange={this.handleChange}
                        placeholder={this.state.location}
                        required
                      ></input>
                    </div>
                  </div>
                  <div className="row">
                    <div className="col s12">
                      <label htmlFor="category" className="left indigo-text">
                        Foodtruck's category:
                      </label>
                      <input
                        className="validate"
                        maxLength="20"
                        data-length="20"
                        type="text"
                        name="category"
                        value={this.state.category}
                        onChange={this.handleChange}
                        placeholder={this.state.category}
                        required
                      ></input>
                    </div>
                  </div>
                  <div className="row">
                    <div className="col s12">
                      <label htmlFor="hours" className="left indigo-text">
                        Foodtruck's hours of operation:
                      </label>
                      <input
                        className="validate"
                        data-length="10"
                        type="text"
                        name="hours"
                        value={this.state.hours}
                        onChange={this.handleChange}
                        placeholder={this.state.hours}
                        required
                      ></input>
                    </div>
                  </div>
                  <div className="row">
                    <div className="col s12">
                      <label htmlFor="description" className="left indigo-text">
                        Foodtruck's description:
                      </label>
                      <textarea
                        className="validate materialize-textarea"
                        data-length="120"
                        type="text"
                        name="description"
                        value={this.state.description}
                        onChange={this.handleChange}
                        placeholder={this.state.description}
                        required
                      ></textarea>
                    </div>
                  </div>
                  <div className="row">
                    <div className="col s12">
                      <label htmlFor="score" className="left indigo-text">
                        Foodtruck's score:
                      </label>
                      <input
                        id="input_rangefield"
                        className="validate"
                        type="range"
                        step="1"
                        name="score"
                        min="0"
                        max="10"
                        value={this.state.score}
                        onChange={this.handleChange}
                        placeholder={this.state.score}
                      ></input>
                    </div>
                  </div>
                  <div className="row">
                    <div className="col s12">
                      <button type="submit" className="btn indigo col s12">
                        {this.props.buttonText}
                      </button>
                    </div>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      );
    } else {
      return <Redirect to="/"></Redirect>;
    }
  };
  render() {
    return <div>{this.renderForm()}</div>;
  }
}

export default NewFoodtruck;
