import React from "react";
import { connect } from "react-redux";

const FoodtruckCard = ({ foodtruck, currentAccount, loggedIn }) => {
  if (foodtruck && loggedIn) {
    return (
      <div className="row">
        <div className="col s12 m6">
        <div className="card blue-grey darken-1">
          <h4>{foodtruck.name}</h4>
          <div className="card-content white-text">
            <div className="card-title">
              <p>{foodtruck.description}</p>
            </div>
            <div className="card-action">
              <ul className="tabs tabs-fixed-width">
                <li className="tab" href="moreDetails">
                  more details
                </li>
                <li className="card-action" href="#">
                  edit info
                </li>
                <li className="tab">delete</li>
              </ul>
            </div>
            <div className="card-content grey ligthen-4">
              <div id="moreDetails">
                <p>Category: {foodtruck.category}</p>
                <p>Hours: {foodtruck.hours}</p>
                <p>Location: {foodtruck.location}</p>
                <p>Score: {foodtruck.score}</p>
              </div>
              <div className="edit">
                {" "}
                {foodtruck.account_id === currentAccount.account.action.id ? (
                  <button
                    className="btn btn-indigo sm"
                    href={`/recipes/${foodtruck.id}/edit`}
                  >
                    Edit This Foodtruck
                  </button>
                ) : (
                  "You can only edit foodtrucks that belongs to your account"
                )}
              </div>
            </div>
          </div>
        </div>
            </div>
            </div>
    );
  } else if (foodtruck) {
    return <div className="card">Under Construction</div>;
  }
  return <p>no foodtruck</p>;
};

const mapStateToProps = (state) => {
  return {
    currentAccount: state.currentAccount,
    loggedIn: !!state.currentAccount,
  };
};

export default connect(mapStateToProps)(FoodtruckCard);
