import React, { Component } from 'react'

export default class Form extends Component {
  handleChange = (e) => {
    this.setState({
      comment: e.target.value,
    });
  };
  handleSubmit = (e) => {};
  render() {
    return (
      <div>
        <form action="" onSubmit={this.handleSubmit}>
          <input
            type="text"
            name="comment"
            value={this.state.comment}
            onChange={this.handleChange}
          ></input>
        </form>
      </div>
    );
  }
}
