import React from "react";
import { Nav, Navbar } from "react-bootstrap";
import { logout } from "../actions/currentAccount";
import { connect } from "react-redux";
import { Link } from "react-router-dom";

const NavBar = (props) => {
  const logoutRedirect = () => {
    props.logout();
    alert("You have successfully logged out. Have a nice day!");
    props.history.push("/");
  };

  const renderNavbar = () => {
    if (props.loggedIn && props.currentAccount.account.action) {
      return (
        <div className="nav-wrapper">
          Hi {props.currentAccount.account.action.name}
          <ul id="nav-mobile" className="right center-on-med-and-down">
            <li>
              <Link
                to={`accounts/${props.currentAccount.account.action}/food_trucks`}
              >
                My Foodtrucks
              </Link>
            </li>
          </ul>
        </div>
      );
    } else {
      return (
        <div>
          <div className="nav-wrapper">
            <ul id="nav-mobile" className="right hide-on-med-and-down">
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/signup">Sign Up</Link>
              </li>
              <li>
                <Link to="/login">Login</Link>
              </li>
            </ul>
          </div>
        </div>
      );
    }
  };
  return (
    <div>
      <nav>
        <div className="nav-wrapper">{renderNavbar()}</div>
      </nav>
    </div>
  );
};
const mapStateToProps = (state) => {
  return {
    currentAccount: state.currentAccount,
    loggedIn: !!state.currentAccount,
  };
};
export default connect(mapStateToProps, { logout })(NavBar);
