const initialState = {
  name: "",
  email: "",
  password: "",
};

export default function manageSignupForm(state = initialState, action) {
  switch (action.type) {
    case "UPDATE_SIGNUP_FORM":
      return action.formData;
    case "RESET_SIGNUP_FORM":
      return initialState;
    default:
      return state;
  }
}
