import React from "react";
import { Nav, Navbar } from "react-bootstrap";
import { logout } from "../actions/currentAccount";
import { connect } from "react-redux";
import { Link } from "react-router-dom";

const NavBar = (props) => {
  const logoutRedirect = () => {
    props.logout();
    alert("You have successfully logged out. Have a nice day!");
    props.history.push("/");
  };

  const renderNavbar = () => {
    if (props.loggedIn) {
      return (
        <div className="nav-wrapper">
          Hi {props.currentAccount.account.action.name}
          <ul id="nav-mobile" class="right hide-on-med-and-down"></ul>
          <Link to={`accounts/${props.currentAccount.account.action}/food_trucks`}>My Foodtrucks</Link>
          </div>
      );
    } else {
      return (
        <div>
          <div className="nav-wrapper">
            <Link to="/">Home</Link>

            <Link to="/signup">Sign Up</Link>

            <Link to="/login">Login</Link>
          </div>
        </div>
      );
    }
  };
  return (
    <div>
      <nav>
        <div className="nav-wrapper">{renderNavbar()}</div>
      </nav>
    </div>
  );
};
const mapStateToProps = (state) => {
  return {
    currentAccount: state.currentAccount,
    loggedIn: !!state.currentAccount,
  };
};
export default connect(mapStateToProps, { logout })(NavBar);
