import { resetLoginForm } from "./handleLoginForm";
import { resetSignupForm } from "./handleSignupForm";
import { endPoints } from "../config";

const { login_, signup_, logout_, loggedIn_ } = endPoints;

// synchronous action creators
export const setCurrentAccount = (account) => {
  return {
    type: "SET_CURRENT_ACCOUNT",
    account,
  };
};

export const clearCurrentAccount = () => {
  return {
    type: "CLEAR_CURRENT_ACCOUNT",
  };
};

// asynchronous action creators
export const login = (credentials, history) => {
  return (dispatch) => {
    return fetch("http://localhost:3000/api/v1/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(credentials),
    })
      .then((res) => res.json())
      .then((response) => {
        if (response.error) {
          alert(response.error);
        } else {
          dispatch(setCurrentAccount(response.account));
          console.log(CurrentAccount());
          dispatch(resetLoginForm());
          alert("You have logged in successfully.");
          history.push("/");
          console.log(`response.account: ${response.account}`);
        }
      })
      .catch(console.log);
  };
};

export const signup = (credentials, history) => {
  return (dispatch) => {
    const accountInfo = {
      account: credentials,
    };
    return fetch(signup_, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(accountInfo),
    })
      .then((res) => res.json())
      .then((response) => {
        if (response.error) {
          alert(response.error);
        } else {
          dispatch(setCurrentAccount(response.account));
          dispatch(resetSignupForm());
          history.push("/");
        }
      })
      .catch(console.log);
  };
};

export const logout = () => {
  return (dispatch) => {
    dispatch(clearCurrentAccount());
    return fetch(logout_, {
      method: "DELETE",
    });
  };
};

export const getCurrentAccount = () => {
  return (dispatch, getState) => {
    console.log(getState());
    return fetch(loggedIn_, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((response) => {
        if (response.error) {
          console.log(response.error);
        } else {
          console.log(response.account);
          if (
            response.account.loggedIn &&
            getState().currentAccount.loggedIn === false
          ) {
            dispatch(setCurrentAccount(response.account));
          }
        }
      })
      .catch(console.log);
  };
};
