import React from "react";
import { Nav, Navbar } from "react-bootstrap";
import { logout } from "../actions/currentAccount";
import { connect } from "react-redux";
import { Link } from "react-router-dom";

const NavBar = (props) => {
  const logoutRedirect = () => {
    props.logout();
    alert("You have successfully logged out. Have a nice day!");
    props.history.push("/");
  };

  const renderNavbar = () => {
    if (props.loggedIn && props.currentAccount.account.action.name) {
      return (
        <div className="nav-wrapper">
          Hi {props.currentAccount.account.action.name}
          <ul id="nav-mobile" className="right hide-on-med-and-down"></ul>
          <li>
            <Link
              to={`accounts/${props.currentAccount.account.action}/food_trucks`}
            >
              My Foodtrucks
            </Link>
          </li>
        </div>
      );
    } else {
      return (
        <div>
          <div className="nav-wrapper">
            <Link to="/">Home</Link>

            <Link to="/signup">Sign Up</Link>

            <Link to="/login">Login</Link>
          </div>
        </div>
      );
    }
  };
  return (
    <div>
      <nav>
        <div className="nav-wrapper">{renderNavbar()}aaa</div>
      </nav>
    </div>
  );
};
const mapStateToProps = (state) => {
  return {
    currentAccount: state.currentAccount,
    loggedIn: !!state.currentAccount,
  };
};
export default connect(mapStateToProps, { logout })(NavBar);
