import React, { Component } from "react";

import FoodtruckCard from "../components/FoodtruckCard";

class AccountPage extends Component {
  renderFoodtruckCards = () => {
    const AllFoodtrucks = this.props.foodtrucks;
    const acc = this.props.account;
    if (AllFoodtrucks && AllFoodtrucks.length > 0) {
      debugger;
      return AllFoodtrucks.map((ft) => (
        <FoodtruckCard
          key={ft.id}
          AccountPageFoodtrucks={ft}
          CurrentAccount={acc}
        />
      ));
    } else if (AllFoodtrucks.length === 0 && acc) {

      return (
        <div className="flow-text center">
          no foodtrucks were found, feel free to add one!
        </div>
      );
    } else if (!acc) {
      return (
        <div className="flow-text center">
          Login to add foodtrucks to your profile!
        </div>
      );
    }
  };
  render() {
    return <div className="div">{this.renderFoodtruckCards()}</div>;
  }
}
export default AccountPage;
