import React from "react";
import { Nav, Navbar } from "react-bootstrap";
import { logout } from "../actions/currentAccount";
import { connect } from "react-redux";

const NavBar = (props) => {
  const logoutRedirect = () => {
    props.logout();
    alert("You have successfully logged out. Have a nice day!");
    props.history.push("/");
  };

  const renderNavbar = () => {
    // debugger
    if (props.loggedIn) {
      return (
        <div>
          <Nav.Item>
            <Nav.Link href="/myrecipes">
              {/* Hi, {props.currentAccount.account.attributes?.name} */}
            </Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link>|</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link href="/">Home</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link onClick={logoutRedirect}>Logout</Nav.Link>
          </Nav.Item>
        </div>
      );
    } else {
      return (
        <div>
          <Nav.Item>
            <Nav.Link href="/">Home</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link href="/signup">Sign Up</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link href="/login">Login</Nav.Link>
          </Nav.Item>
        </div>
      );
    }
  };
  return <div>{renderNavbar()}</div>;
};
const mapStateToProps = (state) => {
  return {
    currentUser: state.currentUser,
    loggedIn: !!state.currentUser,
  };
};
export default connect(mapStateToProps, { logout })(NavBar);
