import React, { Component } from "react";
import Navbar from "./components/NavBar";
import NewFoodtruck from "./components/NewFoodtruck";
import Login from "./components/sessions/Login";
import Home from "./containers/Home";
import Signup from "./components/sessions/Signup";
import { connect } from "react-redux";
import { getCurrentAccount } from "./actions/currentAccount";
import { Route, Switch, withRouter, Redirect } from "react-router-dom";
import { getAllFoodtrucks } from "./actions/foodTruck";

class App extends Component {
  componentDidMount() {
    this.props.getCurrentAccount();
    this.props.getAllFoodtrucks();
  }

  render() {
    const { allFoodtrucks, updateFoodtruck } = this.props;
    return (
      <div className="App">
        <Navbar />
        <Switch>
          <Route
            exact
            path="/signup"
            render={({ history }) => <Signup history={history} />}
          />
          <Route path="/accounts/:id/foodtrucks/new" component={NewFoodtruck} />
          <Route exact path="/login" component={Login} />
          <Route exact path="/" component={Home} />
          <Route
            exact
            path="/accounts/:id/foodtrucks/:id/edit"
            render={(props) => {
              const foodTruck = allFoodtrucks.find(
                (foodTruck) => foodTruck.id === props.match.params.id
              );
              return (
                <NewFoodtruck
                  onSubmit={updateFoodtruck}
                  foodTruck={foodTruck}
                />
              );
            }}
          />
          <Route exact path="/accounts/:id/foodtrucks/:id/delete" />
          {/* <Redirect to="/no-match" /> */}
        </Switch>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    allFoodtrucks: state.foodtruckReducer.allFoodtrucks,
  };
};

export default withRouter(
  connect(mapStateToProps, { getCurrentAccount, getAllFoodtrucks })(App)
);
