import React, { Component } from "react";
import { connect } from "react-redux";
import { removeFoodtruck } from "../actions/foodTruck";
class Delete extends Component {
  handleClick = () => {
    if (this.props.allFoodtrucks && this.props.currentAccount) {
      const foodtruck = this.props.allFoodtrucks.find(
        (ft) => ft.id == this.props.currentAccount.account.action.id
      );
      removeFoodtruck(
        foodtruck.id,
        this.props.currentAccount.account.action.id,
        this.props.history
      );
    } else {
      return "error";
    }
  };
  render() {
    return (
      <div >
        <button className="btn indigo center" onClick={this.handleClick}>
          Delete foodtruck
        </button>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    currentAccount: state.currentAccount,
    allFoodtrucks: state.foodtruckReducer.allFoodtrucks,
  };
};

export default connect(mapStateToProps, null)(Delete);
